mapper.select:
SELECT IFNULL(SUM(CHARGE_AMOUNT), 0) AS CHARGE_AMOUNT
FROM T_ASSETS
WHERE SUBSTR(ASSET_AT, 1, 7) = #{ASSET_AT}

mapper.insert:
INSERT INTO T_ASSETS
(
	ASSET_ID
  , ASSET_TYPE
  , CURRENT_BALANCE
  , ASSET_AT
) VALUES (
	#{ASSET_ID}
  , #{ASSET_TYPE}
  , #{CURRENT_BALANCE}
  , SYSDATE()
);

	INSERT INTO T_ACCOUNT_BOOK
	(
		ACCOUNT_ID
	  , ASSET_ID
	  , CATEGORY_ID
	  , ACCOUNT_TYPE
	  , ACCOUNT_DATE
	  , AMOUNT
      , DESCRIPTION
	  , CREATE_DT
	) VALUE (
		#{ACCOUNT_ID}
	  , #{ASSET_ID}
	  , #{CATEGORY_ID}
	  , #{ACCOUNT_TYPE}
	  , DATE_FORMAT(NOW(), '%Y-%m-%d')
	  , #{AMOUNT}
	  , #{DESCRIPTION}
	  , NOW()
	);

mapper.update:
UPDATE T_ASSETS
SET CURRENT_BALANCE = #{CURRENT_BALANCE}
WHERE ASSET_ID = #{ASSET_ID};
