mapper.select:
SELECT A.ACCOUNT_ID , A.ASSET_ID , A.CATEGORY_ID , A.ACCOUNT_TYPE , A.ACCOUNT_DATE , A.AMOUNT , A.DESCRIPTION , A.CREATE_DT , B.CATEGORY_NM FROM T_ACCOUNT_BOOK A INNER JOIN T_CATEGORY B ON A.CATEGORY_ID = B.CATEGORY_ID WHERE 1=1
AND TO_CHAR(A.ACCOUNT_DATE, 'YYYY-MM') = #{searchDate}
AND (A.ACCOUNT_DATE < #{lastAccountDate} OR (A.ACCOUNT_DATE = #{lastAccountDate} AND A.ACCOUNT_ID < #{lastAccountId}))
ORDER BY A.ACCOUNT_DATE DESC, A.ACCOUNT_ID DESC LIMIT #{size}

SELECT IFNULL(SUM(CHARGE_AMOUNT), 0) AS CHARGE_AMOUNT
FROM T_ASSETS
WHERE SUBSTR(ASSET_AT, 1, 7) = #{ASSET_AT}

mapper.insert:
INSERT INTO T_ASSETS
(
	ASSET_ID
  , ASSET_TYPE
  , CURRENT_BALANCE
  , ASSET_AT
) VALUES (
	#{ASSET_ID}
  , #{ASSET_TYPE}
  , #{CURRENT_BALANCE}
  , SYSDATE()
);

	INSERT INTO T_ACCOUNT_BOOK
	(
		ACCOUNT_ID
	  , ASSET_ID
	  , CATEGORY_ID
	  , ACCOUNT_TYPE
	  , ACCOUNT_DATE
	  , AMOUNT
      , DESCRIPTION
	  , CREATE_DT
	) VALUE (
		#{ACCOUNT_ID}
	  , #{ASSET_ID}
	  , #{CATEGORY_ID}
	  , #{ACCOUNT_TYPE}
	  , DATE_FORMAT(NOW(), '%Y-%m-%d')
	  , #{AMOUNT}
	  , #{DESCRIPTION}
	  , NOW()
	);

mapper.update:
UPDATE T_ASSETS
SET CURRENT_BALANCE = #{CURRENT_BALANCE}
WHERE ASSET_ID = #{ASSET_ID};

mapper.select:
SELECT B.CATEGORY_NM , ABS(IFNULL(SUM(CASE WHEN SUBSTR(A.ACCOUNT_DATE, 6, 2) = '01' THEN A.AMOUNT ELSE 0 END), 0)) AS '01' , ABS(IFNULL(SUM(CASE WHEN SUBSTR(A.ACCOUNT_DATE, 6, 2) = '02' THEN A.AMOUNT ELSE 0 END), 0)) AS '02' , ABS(IFNULL(SUM(CASE WHEN SUBSTR(A.ACCOUNT_DATE, 6, 2) = '03' THEN A.AMOUNT ELSE 0 END), 0)) AS '03' , ABS(IFNULL(SUM(CASE WHEN SUBSTR(A.ACCOUNT_DATE, 6, 2) = '04' THEN A.AMOUNT ELSE 0 END), 0)) AS '04' , ABS(IFNULL(SUM(CASE WHEN SUBSTR(A.ACCOUNT_DATE, 6, 2) = '05' THEN A.AMOUNT ELSE 0 END), 0)) AS '05' , ABS(IFNULL(SUM(CASE WHEN SUBSTR(A.ACCOUNT_DATE, 6, 2) = '06' THEN A.AMOUNT ELSE 0 END), 0)) AS '06' , ABS(IFNULL(SUM(CASE WHEN SUBSTR(A.ACCOUNT_DATE, 6, 2) = '07' THEN A.AMOUNT ELSE 0 END), 0)) AS '07' , ABS(IFNULL(SUM(CASE WHEN SUBSTR(A.ACCOUNT_DATE, 6, 2) = '08' THEN A.AMOUNT ELSE 0 END), 0)) AS '08' , ABS(IFNULL(SUM(CASE WHEN SUBSTR(A.ACCOUNT_DATE, 6, 2) = '09' THEN A.AMOUNT ELSE 0 END), 0)) AS '09' , ABS(IFNULL(SUM(CASE WHEN SUBSTR(A.ACCOUNT_DATE, 6, 2) = '10' THEN A.AMOUNT ELSE 0 END), 0)) AS '10' , ABS(IFNULL(SUM(CASE WHEN SUBSTR(A.ACCOUNT_DATE, 6, 2) = '11' THEN A.AMOUNT ELSE 0 END), 0)) AS '11' , ABS(IFNULL(SUM(CASE WHEN SUBSTR(A.ACCOUNT_DATE, 6, 2) = '12' THEN A.AMOUNT ELSE 0 END), 0)) AS '12' , ABS(IFNULL(SUM(A.AMOUNT), 0)) AS TOTAL FROM T_ACCOUNT_BOOK A INNER JOIN T_CATEGORY B ON A.CATEGORY_ID = B.CATEGORY_ID WHERE A.ACCOUNT_TYPE = 'O' AND SUBSTR(A.ACCOUNT_DATE, 1, 4) = #{year}
AND SUBSTR(A.ACCOUNT_DATE, 6, 2) = #{month}
GROUP BY B.CATEGORY_NM
